(function($) {
    $.fn.extend({leanModal: function(options) {
            var defaults = {top: 0, overlay: 0.5, closeButton: null};
            var overlay = $("<div id='lean_overlay'></div>");
            $("body").append(overlay);
            options = $.extend(defaults, options);
            return this.each(function() {
                var o = options;
                $(this).click(function(e) {
                    var modal_id = $(this).attr("href");
                    var id = $(this).attr("relID");
                    var action = $(this).attr("action");
                    if (id) {
                        $.ajax({
                            type: "POST",
                            url: "/content/group/" + action,
                            data: {id: id},
                            cache: false,
                            success: function(data) {
                                var obj = jQuery.parseJSON(data);
                                if (action == 'removeGroup')
                                    $('#successRemoveGroup').html('<input type=hidden name=userID value=' + id + '><p>Are you sure you want to remove ' + obj.name + '?</p>');
                                else
                                    $('#successAdmin').html('<input type=hidden name=userID value=' + id + '><p>As a group admin, ' + obj.name + ' will be able to edit group settings, remove members and give other members admin status.</p>');
                            }
                        })
                    }
//                    $("#lean_overlay").click(function() {
//                        close_modal(modal_id)
//                    });
                    $(o.closeButton).click(function() {
                        close_modal(modal_id)
                    });
                    var modal_height = $(modal_id).outerHeight();
                    var modal_width = $(modal_id).outerWidth();
                    $("#lean_overlay").css({"display": "block", opacity: 0});
                    $("#lean_overlay").fadeTo(100, o.overlay);
                    $(modal_id).css({"display": "block", "position": "fixed", "opacity": 0, "z-index": 11000, "left": 50 + "%", "margin-left": -(modal_width / 2) + "px", "top": o.top + "px"});
                    $(modal_id).fadeTo(100, 1);
                    e.preventDefault()
                })
            });
            function close_modal(modal_id) {
                $("#lean_overlay").fadeOut(200);
                $(modal_id).css({"display": "none"})
            }
        }
    })

})(jQuery);
